  begin
    select   code
      into   vva_code
      from   vd_i_sie_01_lang l
     where   l.seqnc = obten_lang_sesn;

    return vva_code;
  end obten_code_lang_sesn;

  function obten_lang_formt (pnu_lang in number)
    return varchar2 is
  begin
    return null;
