    procedure ajout_liste_valrs (pva_liste in typ_liste_valrs) is
    begin
      if pva_liste.first () is not null then
        for i in pva_liste.first () .. pva_liste.last () loop
          vin_index := vin_index + 1;
          vty_liste_fusn (vin_index) := pva_liste (i);
        end loop;
      end if;
    end;
  begin
