  begin
    vbo_date_valid :=
      (pva_date_a_valdr >= pda_date_limt_infr
    or pda_date_limt_infr is null);
    vbo_date_valid :=
      vbo_date_valid
  and (pva_date_a_valdr <= pda_date_limt_supr
    or pda_date_limt_supr is null);
    return vbo_date_valid;
