  function valr_numrq (pva_valr in varchar2)
    return number is
    vva_valr                                varchar2 (4000);
  begin
    vva_valr :=
      replace (pva_valr,
               ' ',
               null);
    vva_valr :=
      replace (vva_valr,
               '$',
               null);
    vva_valr :=
      replace (vva_valr,
               '.',
               ',');
    return vva_valr;
  end;

