  function obten_lang_furtr
    return varchar2 is
    vva_lang                                varchar2 (100);
  begin
    vva_lang := owa_util.get_cgi_env ('HTTP_ACCEPT_LANGUAGE');

    if instr (vva_lang,
              ',') > 0 then
      vva_lang :=
        substr (vva_lang,
                1,
                instr (vva_lang,
                       ',') -
                1);
    end if;

    if instr (vva_lang,
              ';') > 0 then
      vva_lang :=
        substr (vva_lang,
                1,
                instr (vva_lang,
                       ';') -
                1);
    end if;

    return vva_lang;
  --return upper(aa.g_browser_language);
  end obten_lang_furtr;

