  procedure defnr_parmt_url_apres_authe (pnu_numr_apex_aplic in number default sie_11_aplic_pkg.obten_numr_apex_aplic,
                                         pnu_numr_apex_page in number default sie_13_page_pkg.obten_numr_apex_page,
