  begin
    select   pc.seqnc
      into   pnu_seqnc
      from   vd_i_sie_17_profl_courl pc
     where   pc.code = pva_code_profl_courl and pc.ref_domn = sie_12_domn_pkg.obten_seqnc_domn (pva_code_domn);

