    --Ajouter les librairies JavaScript
    sie_21_util_pkg.ajout_libr_javsc ('afw.sie_21.actio_dynmq.soumt_blob',
                                      sie_11_prodt_pkg.obten_dosr_fichr_afw || 'plugin/javascript/');

