        --|| pnu_numr_apex_regn
        || '_'
        || p_rec_arbre.vva_valr
        || '"'
        || ',"rel":"'
        || case
             when p_rec_arbre.vva_type is not null then p_rec_arbre.vva_type
             else obten_plugn_arbre_noeud_deft (pnu_numr_apex_regn)
           end
        || '"'
        || '},'
        || '"data":{"title":"'
