                                     'Acceder au produit.');
          sie_15_htp_pkg.ouvri_bals ('span',
                                     'class',
                                     'ui-button-icon-secondary ui-icon ui-icon-extlink');
          sie_15_htp_pkg.fermr_bals ('span');
          sie_15_htp_pkg.fermr_bals ('a');
        end if;
        sie_15_htp_pkg.fermr_bals ('span');
        sie_15_htp_pkg.fermr_bals ('li');
      end loop;
      sie_15_htp_pkg.fermr_bals ('ul');
