    rec_regn                                sie_21_util_pkg.t_regn_rendr_reslt;

    procedure genr_regn (p_regn in sie_21_util_pkg.t_regn) is
      procedure genr_lien_aide_page is
      begin
        sie_15_htp_pkg.ouvri_bals ('a',
                                   'href',
                                   '#',
                                   'alt',
                                   'Afficher l''aide de la page',
                                   'title',
