        --if wwv_flow_api.p_ALLOW_FEEDBACK_YN = 'Y' then
        /*
        sie_15_htp_pkg.ouvri_bals ('a'
                                  ,'id'
                                  ,'boutn_comnt_bogue'
                                  ,'href'
                                  ,'#'
                                  ,'alt'
