        sie_15_htp_pkg.ouvri_bals ('i', 'class', 'icon-bug');
        sie_15_htp_pkg.fermr_bals ('i');

        sie_15_htp_pkg.ecrir ('Soumettre vos commentaires et bogues');
        sie_15_htp_pkg.fermr_bals ('a');
        */
        --end if;

        sie_15_htp_pkg.fermr_bals ('div');
